<#
    .SYNOPSIS
        Adds a shortcut to a web app using the default browser.
    
    .DESCRIPTION
        Author:  Axel Timmermans
        Version: 1.1

        Adds a shortcut to something, on the Public desktop and public start menu, or other locations you can add yourself.
        Deploy as PowerShell script, assign to users or devices, do not deploy with logged on user's credentials (will deploy on device eventually, not on users)
#>

Param(
        [Parameter(Mandatory=$False)]$Option # Install for installation of shortcuts and Uninstall for removal of shortcuts

    )
    $Option = "Install"

#Variables
    [string]$ShortcutName          = "Google Earth" #Name of icon, for example Google Earth
    [string]$ShortcutUrl           = "`"${env:ProgramFiles(x86)}\Google\Chrome\Application\chrome.exe`"" #Web address or path to application
    [string]$ShortcutArgs          = "https://earth.google.com/web/" #If $ShortcutURL is set to a location, enter arguments here. May be left blank if there are none
    [string]$ShortcutIconLocation  = "$env:SystemRoot\System32\Google Earth.ico" #File the ico will be written to from Base64 string
    $ShortcutLocations             = @()
    $ShortcutLocations             += "$env:PUBLIC\Desktop"
    $ShortcutLocations             += "$env:ProgramData\Microsoft\Windows\Start Menu\Programs"

#Write shortcut file from Base64 string
    $B64 = ''
    $bytes = [Convert]::FromBase64String($b64)
    [IO.File]::WriteAllBytes($ShortcutIconLocation, $bytes)

#Create
    If($Option -eq "Install"){
        #Create  WScript.Shell object
        $WScriptShell = New-Object -ComObject WScript.Shell

        #Create shortcuts
        ForEach($ShortcutLocation in $ShortcutLocations) {
            $Shortcut = $WScriptShell.CreateShortcut("$ShortcutLocation\$ShortcutName.lnk")
            $Shortcut.TargetPath = $ShortcutUrl
            $Shortcut.Arguments = $ShortcutArgs
            if ($ShortcutIconLocation) {
                $Shortcut.IconLocation = $ShortcutIconLocation
            }
            $Shortcut.Save()
        }
    }

#Remove
    If($Option -eq "Uninstall"){
        ForEach($ShortcutLocation in $ShortcutLocations) {
            Remove-Item -Path "$ShortcutLocation\$ShortcutName.lnk" -Force
        }
    }